C++<pre><code>void Graph::breadth_first_traversal(Vertex *first) const {
    unordered_map&lt;Vertex *, int&gt; hash;
    hash.insert(first);
    std::queue&lt;Vertex *&gt; queue;
    queue.push(first);

    while (!queue.empty()) {
        Vertex *v = queue.front();
        queue.pop();
        // Perform an operation on v

        for (Vertex *w : v-&gt;adjacent_vertices()) {
            if (!hash.member(w)) {
                hash.insert(w);
                queue.push(w);
            }
        }
    }
}
</code></pre>

C++<pre><code>template &lt;typename Type&gt;
void insertion_sort(Type *const array, int const n) {
  for (int k = 1; k &lt; n; ++k) {
    for (int j = k; j &gt; 0; --j) {
      if (array[j - 1] &gt; array[j]) {
        std::swap(array[j - 1], array[j]);
      } else {
        // Stop if the element is already in the correct position
        break;
      }
    }
  }
}
</code></pre> <p>Using <code>std::swap</code> to insert the element into correct position.</p>

C++<pre><code>template &lt;typename Type&gt;
void insertion_sort_optimized(Type *const array, int const n) {
  for (int k = 1; k &lt; n; ++k) {
    Type tmp = array[k];
    int j = k;
    while (j &gt; 0 &amp;&amp; array[j - 1] &gt; tmp) {
      array[j] = array[j - 1];
      --j;
    }
    array[j] = tmp;
  }
}
</code></pre>

C++<pre><code>template &lt;typename Type&gt;
void bubble_sort(Type *const array, int const n) {
  for (int i = n - 1; i &gt; 0; --i) {
    for (int j = 0; j &lt; i; ++j) {
      if (array[j] &gt; array[j + 1]) {
        std::swap(array[j], array[j + 1]);
      }
    }
  }
}
</code></pre>

C++<pre><code>void merge(vector&lt;int&gt; &amp;nums, int left, int mid, int right) {
  vector&lt;int&gt; tmp(right - left + 1);
  int i = left, j = mid + 1, k = 0;
  while (i &lt;= mid &amp;&amp; j &lt;= right) {
    if (nums[i] &lt;= nums[j])
      tmp[k++] = nums[i++];
    else
      tmp[k++] = nums[j++];
  }
  while (i &lt;= mid) {
    tmp[k++] = nums[i++];
  }
  while (j &lt;= right) {
    tmp[k++] = nums[j++];
  }
  for (k = 0; k &lt; tmp.size(); k++) {
    nums[left + k] = tmp[k];
  }
}

void merge_sort(vector&lt;int&gt; &amp;nums, int left, int right) {
  if (left &gt;= right)
    return;
  int mid = left + (right - left) / 2;
  merge_sort(nums, left, mid);
  merge_sort(nums, mid + 1, right);
  merge(nums, left, mid, right);
}
</code></pre>

C++<pre><code>/* Partition */
int partition(vector&lt;int&gt; &amp;nums, int left, int right) {
    // Use nums[left] as the pivot
    int i = left, j = right;
    while (i &lt; j) {
        while (i &lt; j &amp;&amp; nums[j] &gt;= nums[left])
            j--; // Search from right to left for the first element smaller than the pivot
        while (i &lt; j &amp;&amp; nums[i] &lt;= nums[left])
            i++;          // Search from left to right for the first element greater than the pivot
        swap(nums, i, j); // Swap these two elements
    }
    swap(nums, i, left); // Swap the pivot to the boundary between the two subarrays
    return i;            // Return the index of the pivot
}
/* Quick sort */
void quickSort(vector&lt;int&gt; &amp;nums, int left, int right) {
    // Terminate recursion when subarray length is 1
    if (left &gt;= right)
        return;
    // Partition
    int pivot = partition(nums, left, right);
    // Recursively process the left subarray and right subarray
    quickSort(nums, left, pivot - 1);
    quickSort(nums, pivot + 1, right);
}
</code></pre>

