Python<pre><code>def gcd(a, b):
    while b != 0:
        a, b = b, a % b
    return a

def is_generator(g, n):
    seen = set()
    for k in range(1, n):
        seen.add(pow(g, k, n))
    return len(seen) == n - 1

def find_generators(n):
    generators = []
    for g in range(1, n):
        if gcd(g, n) == 1:  # g 与 n 互质
            if is_generator(g, n):
                generators.append(g)
    return generators

print(find_generators(107))
